import React, {useState} from 'react';
import './ExpenseForm.css';

function ExpenseForm (props){
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    // const[ userInput, setUserInput] = useState({
    //   enteredTitle: '',
    //   enteredAmount: '',
    //   enteredDate:''
    // })

    const handleTitleClick = (event) => {
       setEnteredTitle(event.target.value);

      // setUserInput(()=>{
      //   return {...userInput,enteredTitle: event.target.value}
      // })
    }

    const handleAmountClick = (event) =>{
        setEnteredAmount(event.target.value);
        // setUserInput(()=>{
        //   return {...userInput,enteredAmount: event.target.value}
        // })
    }

    const handleDateClick = (event) =>{
        setEnteredDate(event.target.value);
        // setUserInput(()=>{
        //   return {...userInput,enteredDate: event.target.value}
        // })
    }

    const submitHandler = (event) =>{
      event.preventDefault();
      const expenseData = {
        title: enteredTitle,
        amount: enteredAmount,
        date: new Date(enteredDate)
      };

      props.onSaveExpenseData(expenseData);

      setEnteredTitle('');
      setEnteredAmount('');
      setEnteredDate('');
    }

  return (
    <form onSubmit={submitHandler}>
      <div className='new-expense__controls'>
        <div className='new-expense__control'>
          <label>Title</label>
          <input type='text' value={enteredTitle} onChange={handleTitleClick} />
        </div>
        <div className='new-expense__control'>
          <label>Amount</label>
          <input type='number' min='0.01' step='0.01' value={enteredAmount} onChange={handleAmountClick} />
        </div>
        <div className='new-expense__control'>
          <label>Date</label>
          <input type='date' min='2019-01-01' max='2022-12-31' value={enteredDate} onChange={handleDateClick}/>
        </div>
      </div>
      <div className='new-expense__actions'>
        <button>Cancel</button>
        <button type='submit'>Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
